  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

:root {
  --radius: 0.625rem;
  /* Warehouse blue theme */
  --background: oklch(0.98 0.005 250);
  --foreground: oklch(0.2 0.02 250);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.2 0.02 250);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.2 0.02 250);
  /* Primary: Deep warehouse blue */
  --primary: oklch(0.45 0.15 250);
  --primary-foreground: oklch(0.98 0 0);
  /* Secondary: Light steel gray */
  --secondary: oklch(0.94 0.01 250);
  --secondary-foreground: oklch(0.3 0.02 250);
  --muted: oklch(0.95 0.005 250);
  --muted-foreground: oklch(0.5 0.02 250);
  /* Accent: Bright orange (logistics/warehouse) */
  --accent: oklch(0.72 0.18 50);
  --accent-foreground: oklch(0.2 0.02 50);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.01 250);
  --input: oklch(0.92 0.01 250);
  --ring: oklch(0.5 0.12 250);
  /* Charts */
  --chart-1: oklch(0.45 0.15 250);
  --chart-2: oklch(0.72 0.18 50);
  --chart-3: oklch(0.6 0.15 150);
  --chart-4: oklch(0.65 0.12 300);
  --chart-5: oklch(0.55 0.2 30);
  /* Sidebar */
  --sidebar: oklch(0.25 0.05 250);
  --sidebar-foreground: oklch(0.95 0.01 250);
  --sidebar-primary: oklch(0.72 0.18 50);
  --sidebar-primary-foreground: oklch(0.15 0.02 50);
  --sidebar-accent: oklch(0.35 0.06 250);
  --sidebar-accent-foreground: oklch(0.95 0.01 250);
  --sidebar-border: oklch(0.35 0.04 250);
  --sidebar-ring: oklch(0.5 0.1 250);
  /* Custom inventory status colors */
  --status-in-stock: oklch(0.65 0.18 145);
  --status-low-stock: oklch(0.75 0.18 85);
  --status-out-of-stock: oklch(0.6 0.22 25);
  --status-in-stock-bg: oklch(0.92 0.04 145);
  --status-low-stock-bg: oklch(0.94 0.06 85);
  --status-out-of-stock-bg: oklch(0.92 0.06 25);
}

.dark {
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
