input.new_string:
@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

/* Custom status badge colors */
.status-in-stock {
  background-color: var(--status-in-stock-bg);
  color: var(--status-in-stock);
}

.status-low-stock {
  background-color: var(--status-low-stock-bg);
  color: var(--status-low-stock);
}

.status-out-of-stock {
  background-color: var(--status-out-of-stock-bg);
  color: var(--status-out-of-stock);
}

/* Subtle grid pattern for warehouse feel */
.warehouse-pattern {
  background-image:
    linear-gradient(to right, oklch(0.85 0.01 250 / 30%) 1px, transparent 1px),
    linear-gradient(to bottom, oklch(0.85 0.01 250 / 30%) 1px, transparent 1px);
  background-size: 40px 40px;
}

.dark .warehouse-pattern {
  background-image:
    linear-gradient(to right, oklch(0.3 0.02 250 / 20%) 1px, transparent 1px),
    linear-gradient(to bottom, oklch(0.3 0.02 250 / 20%) 1px, transparent 1px);
}

/* Smooth animations */
@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-slide-in {
  animation: slideIn 0.3s ease-out;
}

