    >
      <CardContent className="pt-6">
        <div className="flex items-center justify-between">
          <div>
            <p
              className={cn(
                "text-sm font-medium",
                variant === "default" && "text-muted-foreground"
              )}
            >
              {title}
            </p>
            <p className="text-3xl font-bold mt-1">{value}</p>
            {trend && (
              <p
                className={cn(
                  "text-xs mt-2 font-medium",
                  trend.isPositive
                    ? "text-[var(--status-in-stock)]"
                    : "text-[var(--status-out-of-stock)]"
                )}
              >
                {trend.isPositive ? "‚Üë" : "‚Üì"} {Math.abs(trend.value)}%
              </p>
            )}
          </div>
          <div
            className={cn(
              "p-3 rounded-xl",
              variant === "default" && "bg-secondary",
              variant === "primary" && "bg-primary-foreground/10",
              variant === "accent" && "bg-accent-foreground/10"
            )}
          >
            <Icon className="h-6 w-6" />
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Pencil, Trash2 } from "lucide-react";
import { cn } from "@/lib/utils";

export interface InventoryItem {
  record_id: string;
  name: string;
  sku: string;
  category: string;
  category_name?: string;
  location: string;
  location_name?: string;
  quantity: number;
  min_quantity: number;
  unit_price: number;
}

interface InventoryTableProps {
  items: InventoryItem[];
  onEdit: (item: InventoryItem) => void;
  onDelete: (id: string) => void;
}

function getStockStatus(quantity: number, minQuantity: number) {
  if (quantity === 0) return { label: "Nicht verf√ºgbar", class: "status-out-of-stock" };
  if (quantity <= minQuantity) return { label: "Niedriger Bestand", class: "status-low-stock" };
  return { label: "Auf Lager", class: "status-in-stock" };
}

export function InventoryTable({ items, onEdit, onDelete }: InventoryTableProps) {
  return (
    <div className="rounded-lg border bg-card overflow-hidden">
      <Table>
        <TableHeader>
          <TableRow className="bg-secondary/50">
            <TableHead>Artikelnr.</TableHead>
            <TableHead>Name</TableHead>
            <TableHead>Kategorie</TableHead>
            <TableHead>Lagerort</TableHead>
            <TableHead className="text-right">Menge</TableHead>
            <TableHead className="text-right">St√ºckpreis</TableHead>
            <TableHead>Status</TableHead>
            <TableHead className="text-right">Aktionen</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {items.length === 0 ? (
            <TableRow>
              <TableCell colSpan={8} className="text-center py-8 text-muted-foreground">
                Keine Artikel gefunden
              </TableCell>
            </TableRow>
          ) : (
            items.map((item) => {
              const status = getStockStatus(item.quantity, item.min_quantity);
              return (
                <TableRow key={item.record_id} className="animate-slide-in">
                  <TableCell className="font-mono text-sm">{item.sku}</TableCell>
                  <TableCell className="font-medium">{item.name}</TableCell>
                  <TableCell>{item.category_name || item.category}</TableCell>
                  <TableCell>{item.location_name || item.location}</TableCell>
                  <TableCell className="text-right">
                    <span
                      className={cn(
                        "font-medium",
                        item.quantity <= item.min_quantity && "text-[var(--status-out-of-stock)]"
                      )}
                    >
                      {item.quantity}
                    </span>
                    <span className="text-muted-foreground"> / Min: {item.min_quantity}</span>
                  </TableCell>
                  <TableCell className="text-right">
                    ‚Ç¨{item.unit_price.toFixed(2)}
                  </TableCell>
                  <TableCell>
                    <Badge variant="outline" className={cn("font-medium", status.class)}>
                      {status.label}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end gap-1">
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => onEdit(item)}
                      >
                        <Pencil className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => onDelete(item.record_id)}
                        className="text-destructive hover:text-destructive"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              );
            })
          )}
        </TableBody>
      </Table>
    </div>
  );
}

import { useEffect, useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { InventoryItem } from "./InventoryTable";

interface Category {
  record_id: string;
  name: string;
}

interface Location {
  record_id: string;
  name: string;
  code: string;
}

interface InventoryDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSubmit: (data: Omit<InventoryItem, "record_id"> | InventoryItem) => void;
  editItem?: InventoryItem | null;
  categories: Category[];
  locations: Location[];
}

export function InventoryDialog({
  open,
  onOpenChange,
  onSubmit,
  editItem,
  categories,
  locations,
}: InventoryDialogProps) {
  const [formData, setFormData] = useState({
    name: "",
    sku: "",
    category: "",
    location: "",
    quantity: 0,
    min_quantity: 5,
    unit_price: 0,
  });

  useEffect(() => {
    if (editItem) {
      setFormData({
        name: editItem.name,
        sku: editItem.sku,
        category: editItem.category,
        location: editItem.location,
        quantity: editItem.quantity,
        min_quantity: editItem.min_quantity,
        unit_price: editItem.unit_price,
      });
    } else {
      setFormData({
        name: "",
        sku: "",
        category: "",
        location: "",
        quantity: 0,
        min_quantity: 5,
        unit_price: 0,
      });
    }
  }, [editItem, open]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editItem) {
      onSubmit({ ...formData, record_id: editItem.record_id });
    } else {
      onSubmit(formData);
    }
    onOpenChange(false);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>
            {editItem ? "Artikel bearbeiten" : "Neuen Artikel hinzuf√ºgen"}
          </DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit}>
          <div className="grid gap-4 py-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="name">Artikelname</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) =>
                    setFormData({ ...formData, name: e.target.value })
                  }
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="sku">Artikelnummer (SKU)</Label>
                <Input
                  id="sku"
                  value={formData.sku}
                  onChange={(e) =>
                    setFormData({ ...formData, sku: e.target.value })
                  }
                  required
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="category">Kategorie</Label>
                <Select
                  value={formData.category}
                  onValueChange={(value) =>
                    setFormData({ ...formData, category: value })
                  }
                >
                  <SelectTrigger>
                    <SelectValue placeholder="W√§hlen..." />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((cat) => (
                      <SelectItem key={cat.record_id} value={cat.record_id}>
                        {cat.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="location">Lagerort</Label>
                <Select
                  value={formData.location}
                  onValueChange={(value) =>
                    setFormData({ ...formData, location: value })
                  }
                >
                  <SelectTrigger>
                    <SelectValue placeholder="W√§hlen..." />
                  </SelectTrigger>
                  <SelectContent>
                    {locations.map((loc) => (
                      <SelectItem key={loc.record_id} value={loc.record_id}>
                        {loc.name} ({loc.code})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="grid grid-cols-3 gap-4">
              <div className="space-y-2">
                <Label htmlFor="quantity">Menge</Label>
                <Input
                  id="quantity"
                  type="number"
                  min="0"
                  value={formData.quantity}
                  onChange={(e) =>
                    setFormData({ ...formData, quantity: parseInt(e.target.value) || 0 })
                  }
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="min_quantity">Mindestmenge</Label>
                <Input
                  id="min_quantity"
                  type="number"
                  min="0"
                  value={formData.min_quantity}
                  onChange={(e) =>
                    setFormData({ ...formData, min_quantity: parseInt(e.target.value) || 0 })
                  }
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="unit_price">St√ºckpreis (‚Ç¨)</Label>
                <Input
                  id="unit_price"
                  type="number"
                  min="0"
                  step="0.01"
                  value={formData.unit_price}
                  onChange={(e) =>
                    setFormData({ ...formData, unit_price: parseFloat(e.target.value) || 0 })
                  }
                  required
                />
              </div>
            </div>
          </div>
          <DialogFooter>
            <Button type="button" variant="outline" onClick={() => onOpenChange(false)}>
              Abbrechen
            </Button>
            <Button type="submit">
              {editItem ? "Speichern" : "Hinzuf√ºgen"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

