// Temporary mock data - will be replaced with LivingApps API
const mockCategories = [
  { record_id: "1", name: "Elektronik", description: "Elektronische Bauteile" },
  { record_id: "2", name: "M√∂bel", description: "B√ºrom√∂bel" },
