];

const mockLocations = [
  { record_id: "1", name: "Halle A", code: "A1-01", description: "Hauptlager" },
  { record_id: "2", name: "Halle A", code: "A1-02", description: "Nebenraum" },
  { record_id: "3", name: "Halle B", code: "B2-15", description: "Au√üenlager" },
];

const mockInventory: InventoryItem[] = [
  {
    record_id: "1",
    name: "Laptop Dell XPS 15",
    sku: "EL-001",
    category: "1",
    category_name: "Elektronik",
    location: "1",
    location_name: "Halle A (A1-01)",
    quantity: 25,
    min_quantity: 10,
    unit_price: 1299.99,
  },
  {
    record_id: "2",
    name: "B√ºrostuhl Ergonomisch",
    sku: "MO-001",
    category: "2",
    category_name: "M√∂bel",
    location: "2",
    location_name: "Halle A (A1-02)",
    quantity: 8,
    min_quantity: 10,
    unit_price: 349.99,
  },
  {
    record_id: "3",
    name: "Akkuschrauber Bosch",
    sku: "WZ-001",
    category: "3",
