  const handleAddLocation = (data: { name: string; code: string; description?: string; record_id?: string }) => {
    if (data.record_id) {
      setLocations((prev) =>
        prev.map((loc) => (loc.record_id === data.record_id ? { ...loc, ...data } : loc))
      );
    } else {
      setLocations((prev) => [
        ...prev,
        { record_id: Date.now().toString(), name: data.name, code: data.code, description: data.description },
      ]);
    }
    setEditLocation(null);
  };

  const handleDeleteLocation = (id: string) => {
    if (confirm("M√∂chten Sie diesen Lagerort wirklich l√∂schen?")) {
      setLocations((prev) => prev.filter((loc) => loc.record_id !== id));
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Laden...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background warehouse-pattern">
      <div className="container mx-auto p-6 max-w-7xl">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 rounded-lg bg-primary">
              <Warehouse className="h-6 w-6 text-primary-foreground" />
            </div>
            <h1 className="text-3xl font-bold text-foreground">Lagerverwaltung</h1>
          </div>
          <p className="text-muted-foreground">
            Verwalten Sie Ihren Lagerbestand effizient und behalten Sie den √úberblick.
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <StatsCard
            title="Gesamtbestand"
            value={totalItems.toLocaleString()}
            icon={Package}
            trend={{ value: 12, isPositive: true }}
          />
          <StatsCard
            title="Niedriger Bestand"
            value={lowStockItems}
            icon={AlertTriangle}
            variant="default"
          />
          <StatsCard
            title="Nicht verf√ºgbar"
            value={outOfStockItems}
            icon={Package}
            variant="default"
          />
          <StatsCard
            title="Gesamtwert"
            value={`‚Ç¨${totalValue.toLocaleString("de-DE", { minimumFractionDigits: 2 })}`}
            icon={TrendingUp}
            variant="primary"
          />
        </div>

        {/* Main Content */}
        <Tabs defaultValue="inventory" className="space-y-4">
          <TabsList className="bg-secondary/50">
            <TabsTrigger value="inventory" className="gap-2">
              <Package className="h-4 w-4" />
              Inventar
            </TabsTrigger>
            <TabsTrigger value="categories" className="gap-2">
              <FolderOpen className="h-4 w-4" />
              Kategorien
            </TabsTrigger>
            <TabsTrigger value="locations" className="gap-2">
              <MapPin className="h-4 w-4" />
              Lagerorte
            </TabsTrigger>
          </TabsList>

          <TabsContent value="inventory" className="space-y-4">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex flex-col sm:flex-row justify-between gap-4">
                  <CardTitle className="text-xl">Artikel√ºbersicht</CardTitle>
                  <div className="flex gap-2">
                    <div className="relative flex-1 sm:w-64">
                      <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                      <Input
                        placeholder="Suchen..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="pl-9"
                      />
                    </div>
                    <Button
                      onClick={() => {
                        setEditItem(null);
                        setDialogOpen(true);
                      }}
                      className="gap-2"
                    >
                      <Plus className="h-4 w-4" />
                      Artikel
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <InventoryTable
                  items={filteredItems}
                  onEdit={handleEditItem}
                  onDelete={handleDeleteItem}
                />
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="categories" className="space-y-4">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex justify-between items-center">
                  <CardTitle className="text-xl">Kategorien</CardTitle>
                  <Button
                    onClick={() => {
                      setEditCategory(null);
