     4‚Üí
     5‚Üí// Base Configuration
     6‚Üíconst API_BASE_URL = 'https://my.living-apps.de/rest';
     7‚Üí
     8‚Üí// --- HELPER FUNCTIONS ---
     9‚Üíexport function extractRecordId(url: string | null | undefined): string | null {
    10‚Üí  if (!url) return null;
    11‚Üí  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
    12‚Üí  const match = url.match(/([a-f0-9]{24})$/i);
    13‚Üí  return match ? match[1] : null;
    14‚Üí}
    15‚Üí
    16‚Üíexport function createRecordUrl(appId: string, recordId: string): string {
    17‚Üí  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
    18‚Üí}
    19‚Üí
    20‚Üíasync function callApi(method: string, endpoint: string, data?: any) {
    21‚Üí  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    22‚Üí    method,
    23‚Üí    headers: { 'Content-Type': 'application/json' },
    24‚Üí    credentials: 'include',  // Nutze Session Cookies f√ºr Auth
    25‚Üí    body: data ? JSON.stringify(data) : undefined
    26‚Üí  });
    27‚Üí  if (!response.ok) throw new Error(await response.text());
    28‚Üí  // DELETE returns often empty body or simple status
    29‚Üí  if (method === 'DELETE') return true;
    30‚Üí  return response.json();
    31‚Üí}
    32‚Üí
    33‚Üíexport class LivingAppsService {
    34‚Üí  // --- CATEGORIES ---
