import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { StatsCard } from "@/components/StatsCard";
import { InventoryTable, InventoryItem } from "@/components/InventoryTable";
import { InventoryDialog } from "@/components/InventoryDialog";
import { CategoryDialog } from "@/components/CategoryDialog";
import { LocationDialog } from "@/components/LocationDialog";
import {
  Package,
  Warehouse,
  AlertTriangle,
  TrendingUp,
  Plus,
  Search,
  FolderOpen,
  MapPin,
  Pencil,
  Trash2,
} from "lucide-react";

// Temporary mock data - will be replaced with LivingApps API
const mockCategories = [
  { record_id: "1", name: "Elektronik", description: "Elektronische Bauteile" },
  { record_id: "2", name: "M√∂bel", description: "B√ºrom√∂bel" },
  { record_id: "3", name: "Werkzeug", description: "Handwerkzeuge" },
];

const mockLocations = [
  { record_id: "1", name: "Halle A", code: "A1-01", description: "Hauptlager" },
  { record_id: "2", name: "Halle A", code: "A1-02", description: "Nebenraum" },
  { record_id: "3", name: "Halle B", code: "B2-15", description: "Au√üenlager" },
];

const mockInventory: InventoryItem[] = [
  {
    record_id: "1",
    name: "Laptop Dell XPS 15",
    sku: "EL-001",
    category: "1",
    category_name: "Elektronik",
    location: "1",
    location_name: "Halle A (A1-01)",
    quantity: 25,
    min_quantity: 10,
    unit_price: 1299.99,
  },
  {
    record_id: "2",
    name: "B√ºrostuhl Ergonomisch",
    sku: "MO-001",
    category: "2",
    category_name: "M√∂bel",
    location: "2",
    location_name: "Halle A (A1-02)",
    quantity: 8,
    min_quantity: 10,
    unit_price: 349.99,
  },
  {
    record_id: "3",
    name: "Akkuschrauber Bosch",
    sku: "WZ-001",
    category: "3",
    category_name: "Werkzeug",
    location: "3",
    location_name: "Halle B (B2-15)",
    quantity: 0,
    min_quantity: 5,
    unit_price: 189.99,
  },
  {
    record_id: "4",
    name: "Monitor 27 Zoll 4K",
    sku: "EL-002",
    category: "1",
    category_name: "Elektronik",
    location: "1",
    location_name: "Halle A (A1-01)",
    quantity: 42,
    min_quantity: 15,
    unit_price: 449.99,
  },
];

export default function Dashboard() {
  const [items, setItems] = useState<InventoryItem[]>(mockInventory);
  const [categories, setCategories] = useState(mockCategories);
  const [locations, setLocations] = useState(mockLocations);
  const [searchQuery, setSearchQuery] = useState("");
  const [dialogOpen, setDialogOpen] = useState(false);
  const [categoryDialogOpen, setCategoryDialogOpen] = useState(false);
  const [locationDialogOpen, setLocationDialogOpen] = useState(false);
  const [editItem, setEditItem] = useState<InventoryItem | null>(null);
  const [editCategory, setEditCategory] = useState<typeof mockCategories[0] | null>(null);
  const [editLocation, setEditLocation] = useState<typeof mockLocations[0] | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulating API loading
    setTimeout(() => setLoading(false), 500);
  }, []);

  const filteredItems = items.filter(
    (item) =>
      item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.sku.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const totalItems = items.reduce((sum, item) => sum + item.quantity, 0);
  const lowStockItems = items.filter((item) => item.quantity <= item.min_quantity && item.quantity > 0).length;
  const outOfStockItems = items.filter((item) => item.quantity === 0).length;
  const totalValue = items.reduce((sum, item) => sum + item.quantity * item.unit_price, 0);

  const handleAddItem = (data: Omit<InventoryItem, "record_id"> | InventoryItem) => {
    if ("record_id" in data) {
      // Update existing
      setItems((prev) =>
        prev.map((item) =>
          item.record_id === data.record_id
            ? {
                ...data,
                category_name: categories.find((c) => c.record_id === data.category)?.name,
                location_name: `${locations.find((l) => l.record_id === data.location)?.name} (${locations.find((l) => l.record_id === data.location)?.code})`,
              }
            : item
        )
      );
    } else {
      // Add new
      const newItem: InventoryItem = {
        ...data,
        record_id: Date.now().toString(),
        category_name: categories.find((c) => c.record_id === data.category)?.name,
        location_name: `${locations.find((l) => l.record_id === data.location)?.name} (${locations.find((l) => l.record_id === data.location)?.code})`,
      };
      setItems((prev) => [...prev, newItem]);
    }
    setEditItem(null);
  };

  const handleDeleteItem = (id: string) => {
    if (confirm("M√∂chten Sie diesen Artikel wirklich l√∂schen?")) {
      setItems((prev) => prev.filter((item) => item.record_id !== id));
    }
  };

  const handleEditItem = (item: InventoryItem) => {
    setEditItem(item);
    setDialogOpen(true);
  };

  const handleAddCategory = (data: { name: string; description?: string; record_id?: string }) => {
    if (data.record_id) {
      setCategories((prev) =>
        prev.map((cat) => (cat.record_id === data.record_id ? { ...cat, ...data } : cat))
      );
    } else {
      setCategories((prev) => [
        ...prev,
        { record_id: Date.now().toString(), name: data.name, description: data.description },
      ]);
    }
    setEditCategory(null);
  };

  const handleDeleteCategory = (id: string) => {
    if (confirm("M√∂chten Sie diese Kategorie wirklich l√∂schen?")) {
      setCategories((prev) => prev.filter((cat) => cat.record_id !== id));
    }
  };

  const handleAddLocation = (data: { name: string; code: string; description?: string; record_id?: string }) => {
    if (data.record_id) {
      setLocations((prev) =>
        prev.map((loc) => (loc.record_id === data.record_id ? { ...loc, ...data } : loc))
      );
    } else {
      setLocations((prev) => [
        ...prev,
        { record_id: Date.now().toString(), name: data.name, code: data.code, description: data.description },
      ]);
    }
    setEditLocation(null);
  };

  const handleDeleteLocation = (id: string) => {
    if (confirm("M√∂chten Sie diesen Lagerort wirklich l√∂schen?")) {
      setLocations((prev) => prev.filter((loc) => loc.record_id !== id));
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Laden...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background warehouse-pattern">
      <div className="container mx-auto p-6 max-w-7xl">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 rounded-lg bg-primary">
              <Warehouse className="h-6 w-6 text-primary-foreground" />
            </div>
            <h1 className="text-3xl font-bold text-foreground">Lagerverwaltung</h1>
          </div>
          <p className="text-muted-foreground">
            Verwalten Sie Ihren Lagerbestand effizient und behalten Sie den √úberblick.
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <StatsCard
            title="Gesamtbestand"
            value={totalItems.toLocaleString()}
            icon={Package}
            trend={{ value: 12, isPositive: true }}
          />
          <StatsCard
            title="Niedriger Bestand"
            value={lowStockItems}
            icon={AlertTriangle}
            variant="default"
          />
          <StatsCard
            title="Nicht verf√ºgbar"
            value={outOfStockItems}
            icon={Package}
            variant="default"
          />
          <StatsCard
            title="Gesamtwert"
            value={`‚Ç¨${totalValue.toLocaleString("de-DE", { minimumFractionDigits: 2 })}`}
            icon={TrendingUp}
            variant="primary"
          />
        </div>

        {/* Main Content */}
        <Tabs defaultValue="inventory" className="space-y-4">
          <TabsList className="bg-secondary/50">
            <TabsTrigger value="inventory" className="gap-2">
              <Package className="h-4 w-4" />
              Inventar
            </TabsTrigger>
            <TabsTrigger value="categories" className="gap-2">
              <FolderOpen className="h-4 w-4" />
              Kategorien
            </TabsTrigger>
            <TabsTrigger value="locations" className="gap-2">
              <MapPin className="h-4 w-4" />
              Lagerorte
            </TabsTrigger>
          </TabsList>

          <TabsContent value="inventory" className="space-y-4">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex flex-col sm:flex-row justify-between gap-4">
                  <CardTitle className="text-xl">Artikel√ºbersicht</CardTitle>
                  <div className="flex gap-2">
                    <div className="relative flex-1 sm:w-64">
                      <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                      <Input
                        placeholder="Suchen..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="pl-9"
                      />
                    </div>
                    <Button
                      onClick={() => {
                        setEditItem(null);
                        setDialogOpen(true);
                      }}
                      className="gap-2"
                    >
                      <Plus className="h-4 w-4" />
                      Artikel
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <InventoryTable
                  items={filteredItems}
                  onEdit={handleEditItem}
                  onDelete={handleDeleteItem}
                />
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="categories" className="space-y-4">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex justify-between items-center">
                  <CardTitle className="text-xl">Kategorien</CardTitle>
                  <Button
                    onClick={() => {
                      setEditCategory(null);
                      setCategoryDialogOpen(true);
                    }}
                    className="gap-2"
                  >
                    <Plus className="h-4 w-4" />
                    Kategorie
                  </Button>
                </div>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {categories.map((category) => (
                    <Card key={category.record_id} className="bg-secondary/30">
                      <CardContent className="pt-4">
                        <div className="flex justify-between items-start">
                          <div>
                            <h3 className="font-semibold">{category.name}</h3>
                            <p className="text-sm text-muted-foreground">
                              {category.description || "Keine Beschreibung"}
                            </p>
                            <p className="text-xs text-muted-foreground mt-2">
                              {items.filter((i) => i.category === category.record_id).length} Artikel
                            </p>
                          </div>
                          <div className="flex gap-1">
                            <Button
                              variant="ghost"
                              size="icon-sm"
                              onClick={() => {
                                setEditCategory(category);
                                setCategoryDialogOpen(true);
                              }}
                            >
                              <Pencil className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="icon-sm"
                              onClick={() => handleDeleteCategory(category.record_id)}
                              className="text-destructive hover:text-destructive"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="locations" className="space-y-4">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex justify-between items-center">
                  <CardTitle className="text-xl">Lagerorte</CardTitle>
                  <Button
                    onClick={() => {
                      setEditLocation(null);
                      setLocationDialogOpen(true);
                    }}
                    className="gap-2"
                  >
                    <Plus className="h-4 w-4" />
                    Lagerort
                  </Button>
                </div>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {locations.map((location) => (
                    <Card key={location.record_id} className="bg-secondary/30">
                      <CardContent className="pt-4">
                        <div className="flex justify-between items-start">
                          <div>
                            <h3 className="font-semibold">{location.name}</h3>
                            <p className="text-sm font-mono text-primary">{location.code}</p>
                            <p className="text-sm text-muted-foreground">
                              {location.description || "Keine Beschreibung"}
                            </p>
                            <p className="text-xs text-muted-foreground mt-2">
                              {items.filter((i) => i.location === location.record_id).length} Artikel
                            </p>
                          </div>
                          <div className="flex gap-1">
                            <Button
                              variant="ghost"
                              size="icon-sm"
                              onClick={() => {
                                setEditLocation(location);
                                setLocationDialogOpen(true);
                              }}
                            >
                              <Pencil className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="icon-sm"
                              onClick={() => handleDeleteLocation(location.record_id)}
                              className="text-destructive hover:text-destructive"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>

      {/* Dialogs */}
      <InventoryDialog
        open={dialogOpen}
        onOpenChange={setDialogOpen}
        onSubmit={handleAddItem}
        editItem={editItem}
        categories={categories}
        locations={locations}
      />
      <CategoryDialog
        open={categoryDialogOpen}
        onOpenChange={setCategoryDialogOpen}
        onSubmit={handleAddCategory}
        editItem={editCategory}
      />
      <LocationDialog
        open={locationDialogOpen}
        onOpenChange={setLocationDialogOpen}
        onSubmit={handleAddLocation}
        editItem={editLocation}
      />
    </div>
  );
}

