              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {categories.length === 0 ? (
                    <p className="text-muted-foreground col-span-full text-center py-8">
                      Keine Kategorien vorhanden
