    return callApi('PATCH', `/apps/${APP_IDS.CATEGORIES}/records/${id}`, { fields });
  }
  static async deleteCategorie(id: string) {
    return callApi('DELETE', `/apps/${APP_IDS.CATEGORIES}/records/${id}`);
  }

  // --- LOCATIONS ---
  static async getLocations(): Promise<Locations[]> {
