  }

  // --- GOODS_RECEIPT ---
  static async getGoodsReceipt(): Promise<GoodsReceipt[]> {
    const data = await callApi('GET', `/apps/${APP_IDS.GOODS_RECEIPT}/records`);
    return Object.entries(data).map(([id, rec]: [string, any]) => ({
