    46‚Üí    record_id: cat.record_id,
    47‚Üí    name: cat.fields.name || "",
    48‚Üí    description: cat.fields.description,
    49‚Üí  };
    50‚Üí}
    51‚Üí
    52‚Üífunction transformLocation(loc: Locations): LocationUI {
    53‚Üí  return {
    54‚Üí    record_id: loc.record_id,
    55‚Üí    name: loc.fields.name || "",
    56‚Üí    code: loc.fields.code || "",
    57‚Üí    description: loc.fields.description,
    58‚Üí  };
    59‚Üí}
    60‚Üí
    61‚Üífunction transformInventory(inv: Inventory, categories: CategoryUI[], locations: LocationUI[]): InventoryItem {
    62‚Üí  const categoryId = extractRecordId(inv.fields.category);
    63‚Üí  const locationId = extractRecordId(inv.fields.location);
    64‚Üí  const category = categories.find((c) => c.record_id === categoryId);
    65‚Üí  const location = locations.find((l) => l.record_id === locationId);
    66‚Üí
    67‚Üí  return {
    68‚Üí    record_id: inv.record_id,
    69‚Üí    name: inv.fields.name || "",
    70‚Üí    sku: inv.fields.sku || "",
    71‚Üí    category: categoryId || "",
    72‚Üí    category_name: category?.name || "",
    73‚Üí    location: locationId || "",
    74‚Üí    location_name: location ? `${location.name} (${location.code})` : "",
    75‚Üí    quantity: inv.fields.quantity || 0,
    76‚Üí    min_quantity: inv.fields.min_quantity || 0,
    77‚Üí    unit_price: inv.fields.unit_price || 0,
    78‚Üí  };
    79‚Üí}
